import java.io.*;


log.info  ("......Hello World.....  ");

/* Global variables
 *  log
 *  context
 *  testRunner
 */

 log.info testRunner.metaClass.methods*.name.unique().sort() //This line is used to print all the available methods

class Hello{
	def log;
	def context;
	def testRunner;

	def Hello(log, context, testRunner) {
		this.log = log
		this.context = context
		this.testRunner = testRunner
	}

	def sayHello(String name) {
		log.info("    Saying Hello  "+name);
	}
}
context.setProperty("Hello", new Hello(log,context,testRunner)) //this statement is used to initialize the class, without this statement the clss willnot execute

/* 
here we are calling the class Hello from test case 2 
in this case we have to add below statement
the below 3 statements are used to get access to the class Hello
*/

project = testRunner.testCase.testSuite.project;
tcase = project.testSuites["TestSuite3"].testCases["TestCase1"];
hello = tcase.getTestStepByName("Hello");

hello.run(testRunner, context) //executing the test case Hello

context.Hello.sayHello("Anusha") //calling the method from the class Hello

//running the request from the same test case
